            cleanup
        }
    }, [])


 
    // console.log(movies);
    return (
        <div>
            <h2 className="row_title">{title}</h2>
            <div className = "row_posters">
                {movies.map(movie => (
                    <img 
                        id={movie.id}
                        key={movie.id}
                        className={`row_poster ${isLargeRow && "row_posterLarge"}`}
                        src={`${base_url}${!isLargeRow ? movie.poster_path : movie.backdrop_path}`} 
                        alt={movie.name}
                        onClick={expand}
                    />
                ))}
            </div>
            {isExpanded && <Expanded className="panel_expanded" isOpen={isExpanded} currPoster={currentAsset} >asdasdsad</Expanded>}                       
        </div>
    )
}

export default Row
